const onSignal = '';
const offSignal = '';

const decodeSignalFromHexToBinary = (signal) => {
    let result = [];
    for (const hex of signal.split('')) {
        const bits = parseInt(hex, 16).toString(2).padStart(4, '0');
        result = result.concat(bits.split('').map(Number));
    }
    return result;
};

const on = decodeSignalFromHexToBinary(onSignal);
const off = decodeSignalFromHexToBinary(offSignal);

module.exports = { on, off };
